mapper.select:
select * from member


		where id = #{data}


		where member_name = #{data}


		where email = #{data}


		where phone = #{data}

select * from member
where id = #{id}
and password = #{password}

mapper.update:
update member
set password = #{data}
where id = #{id}

update member
set member_name = #{data}
where id = #{id}

update member
set phone = #{data}
where id = #{id}

update member
set email = #{data}
where id = #{id}
